@import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;500;700;900&display=swap');

body{
    margin: 0;
}

.grid{
    display: grid;
    grid-template-rows: 3.5em 1fr 3.5em;
    grid-template-areas: "Header Header Header"
                             "Main Main Main"
                             "Footer Footer Footer";
    height: 100vh;
}

header{
    grid-area: Header;
    -webkit-box-shadow: 0px 2px 41px -5px rgba(0,0,0,0.40);
    -moz-box-shadow: 0px 2px 41px -5px rgba(0,0,0,0.40);
    box-shadow: 0px 2px 41px -5px rgba(0,0,0,0.40);
}
main{
    grid-area: Main;
}

main .todoButton{
    margin-left: 35%;
    margin-top: 5em;
    width: 7em;
    height: 1.9em;
    background-color: transparent;
    border:solid 1px black ;
    font-family: Roboto,sans-serif;
    font-size: 30px;
}

footer{
    grid-area: Footer;
}
header nav{
    margin-left: 1.4em;
    margin-top: -0.5em;
}

.title a {
    font-family: 'Roboto', sans-serif;
    font-weight: 900;
    font-size: 30px;
    text-decoration: none;
    color: black;
}

.title a:hover{
    color: darkslategrey;
    transition: 0.5s;
    cursor: pointer;
}
.modalTodo form button:hover{
    background-color:#61D195;
    transition: 0.5s;
    cursor: pointer;
}

.todoButton:hover{
    transition: 0.7s;
    background-color: #00AB6F;
    cursor: cell;
}
.closBtn:hover{
    cursor: pointer;
}

.mainModal .create:hover{
    transition: 0.6s;
    background-color: #61D195;
    cursor: pointer;
}

.modalTodo{
    z-index: 9;
    position: absolute;
    top: 50%;
    left: 50%;
    margin-right: -50%;
    transform: translate(-50%, -50%);
    background-color: white;
    width: 340px;
    height: 395px;
    border-radius: 10px;
    -webkit-box-shadow: 0px 0px 49px -24px rgba(0,0,0,0.60);
    -moz-box-shadow: 0px 0px 49px -24px rgba(0,0,0,0.60);
    box-shadow: 0px 0px 49px -24px rgba(0,0,0,0.60);
}

.mainModal{
    z-index: 3;
    position: fixed;
    width: 100%;
    height: 100%;
    backdrop-filter: blur(3px);
    -webkit-backdrop-filter: blur(3px);
    display: none;
}

.mainModal .charLeft{
    display: flex;
    flex-direction: row;
}
.charLeft p:nth-child(2) {
    color: #555555;
}
.charLeft p:nth-child(3) {
    color: #555555;
}

#header{
    display: flex;
    flex-direction: row;
    align-items: center;
    gap: 170px;
    border-bottom: 1px solid #B6B6B6;
}
#header h3{
    margin-left: 15px;
    font-family: Roboto, sans-serif;
    font-weight: 500;
    font-size: 15px;
    color: #555555;
}
#header span{
    padding-left: 60px;
    font-size: 25px;
    color: #B6B6B6;
}

.modalTodo form{
    display: flex;
    flex-direction: column;
}
.modalTodo form input{
    height: 35px;
    width: 300px;
    border-radius: 10px;
    border: solid 1px #B6B6B6;
    margin-left: 1.25em;
    margin-bottom: 0.6em;
    font-family: Roboto, sans-serif;
}

.modalTodo form p{
    font-family: Roboto, sans-serif;
    display: flex;
    margin-bottom: 0.5em;
    margin-left: 1.56em;
    font-size: 13px;
}

.modalTodo form .create{
    background-color: #00AB6F;
    border: none;
    border-radius: 10px;
    width: 13em;
    height: 3em;
    font-family: Roboto, sans-serif;
    font-weight: 600;
    font-size: 15px;
    margin-left: 4.7em;
    margin-top: 13px;
}

.cards{
    margin-top:7em;
    margin-left:35%;
    display: flex;
    flex-direction: row;
    gap: 35px;
}

.card{
    width: 300px;
    height:160px;
    background-color: white;
    border-radius: 10px;
    -webkit-box-shadow: 0px 0px 15px -1px rgba(111,207,252,0.35);
    -moz-box-shadow: 0px 0px 15px -1px rgba(111,207,252,0.35);
    box-shadow: 0px 0px 15px -1px rgba(111,207,252,0.35);
}

.card h3{
    font-family: 'Roboto', sans-serif;
    margin-left: 0.7em;
    padding-top: 10px;
    font-weight: 500;
}

.card p{
    font-family: 'Roboto', sans-serif;
    margin-left: 0.8em;
    font-weight: 300;
    color: #555555;
    margin-top: -10px;
}

.card .removeC{
    background-color:#EF3340;
}
.card .completeC{
    background-color: #56D4C2;

}

.card input{
    width: 100px;
    height: 27px;
    border-radius: 6px;
    border: none;
    margin-left: 10px;
    font-family: 'Roboto', sans-serif;
    font-weight: 500;
}


.completedTodos{
    position: static;
    left: 50%;
    margin-left: 35%;
    margin-top: 60px;
}

.completedTodos h2{
    font-family: 'Roboto', sans-serif;
    margin-bottom: 80px;
}

.compHead{
    display: flex;
    flex-direction: row;
    width: 54em;
    height: 2em;
    background-color: lightgray;
    align-items: center;
    gap: 174px;
}
.compHead h3 {
    color: #18181B;
    margin-left: 30px;
    font-size: 15px;
    font-family: 'Roboto', sans-serif;
}

.TodosInfo{
    display: grid;
    grid-template-columns: 865px;
    grid-auto-rows: 20px;
    grid-row-gap: 25px;

}


.completedInfo{
    grid-column: 1/2;
    align-items: center;
}

.completedInfo ul{
    display: flex;
    list-style-type: none;
    flex-wrap: wrap;
    justify-content: space-between;
    margin:0;
    padding: 0;
    max-width: 100%;
    overflow: hidden;
    border-bottom: solid 1px #bcbcbc;

}

.completedInfo ul li p{
    font-family: 'Roboto', sans-serif;
    color: #bcbcbc;
}
#checkDate{
    margin-top: 10px;
    width:20px;
    height: 20px;
    border: 5px solid black;
}
.date{
    display: flex;
    flex-direction: row;
    gap: 20px;
    margin-left: 43%;
}
.date p{
    font-family: 'Roboto', sans-serif;
    margin-top: 10px;
}
